export class HydroLevelsService {
  private static levelsMap: Map<number, HydroLevel> = new Map();

  static {
    // Inicjalizuj mapę dla szybkiego wyszukiwania
    HYDRO_LEVELS.forEach(level => {
      this.levelsMap.set(level.stationCode, level);
    });
  }

  /**
   * Pobierz poziomy alarmowe dla stacji
   */
  static getLevelsForStation(stationCode: number | string): HydroLevel | null {
    const code = typeof stationCode === 'string' ? parseInt(stationCode) : stationCode;
    return this.levelsMap.get(code) || null;
  }

  /**
   * Określ status alarmowy na podstawie aktualnego poziomu wody
   */
  static getAlarmStatus(stationCode: number | string, currentLevel: number | null): {
    status: 'normal' | 'warning' | 'alarm' | 'unknown';
    warningLevel: number | 'nie określono';
    alarmLevel: number | 'nie określono';
    message: string;
  } {
    if (currentLevel === null || currentLevel === undefined) {
      return {
        status: 'unknown',
        warningLevel: 'nie określono',
        alarmLevel: 'nie określono',
        message: 'Brak danych o poziomie wody'
      };
    }

    const levels = this.getLevelsForStation(stationCode);
    
    if (!levels) {
      return {
        status: 'unknown',
        warningLevel: 'nie określono',
        alarmLevel: 'nie określono',
        message: 'Brak danych o poziomach alarmowych'
      };
    }

    const { warningLevel, alarmLevel } = levels;

    // Jeśli poziomy nie są określone
    if (warningLevel === 'nie określono' || alarmLevel === 'nie określono') {
      return {
        status: 'unknown',
        warningLevel,
        alarmLevel,
        message: 'Poziomy alarmowe nie są określone'
      };
    }

    // Sprawdź status
    if (currentLevel >= alarmLevel) {
      return {
        status: 'alarm',
        warningLevel,
        alarmLevel,
        message: `Przekroczony poziom alarmowy (${alarmLevel} cm)`
      };
    } else if (currentLevel >= warningLevel) {
      return {
        status: 'warning',
        warningLevel,
        alarmLevel,
        message: `Przekroczony poziom ostrzegawczy (${warningLevel} cm)`
      };
    } else {
      return {
        status: 'normal',
        warningLevel,
        alarmLevel,
        message: 'Poziom normalny'
      };
    }
  }

  /**
   * Pobierz wszystkie stacje z określonymi poziomami
   */
  static getStationsWithDefinedLevels(): HydroLevel[] {
    return HYDRO_LEVELS.filter(level => 
      level.warningLevel !== 'nie określono' && 
      level.alarmLevel !== 'nie określono'
    );
  }

  /**
   * Pobierz statystyki poziomów alarmowych
   */
  static getStatistics() {
    const total = HYDRO_LEVELS.length;
    const withDefinedLevels = this.getStationsWithDefinedLevels().length;
    const withoutDefinedLevels = total - withDefinedLevels;

    return {
      total,
      withDefinedLevels,
      withoutDefinedLevels,
      coveragePercentage: Math.round((withDefinedLevels / total) * 100)
    };
  }
} 